import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { FormBuilder } from '@angular/forms';

@Component({
  selector: 'app-articles',
  templateUrl: './articles.component.html',
  styleUrls: ['./articles.component.css']
})


export class ArticlesComponent {
  public articles: Article[] = [];
  addForm = this.fb.group({
    header: '',
    content: ''
  })
  constructor(http: HttpClient, @Inject('BASE_URL') baseUrl: string, private fb: FormBuilder) {
    http.get<Article[]>(baseUrl + 'api/article').subscribe({
      next: (result) => {
        this.articles = result;
      },
      error: (err) => {
        console.error(err);
      }
    });
  }
  onSubmit(): void {
    console.log('submitted form', this.addForm.value);

  }
}


interface Article {
  header: string;
  content: number;
}
<form class="form" [formGroup]="addForm" (ngSubmit)="onSubmit()">
    <div class="field">
        <label class="label">header</label>
        <input type="text" class="input" formControlName="header" />
    </div>
    <div class="field">
        <label class="label">content</label>
        <input type="text" class="input" formControlName="content" />
    </div>

    <div>
        <button type="submit" class="button">Add</button>
    </div>
</form>

<p *ngIf="!articles?.length"><em>Loading...</em></p>

<table class='table table-striped' aria-labelledby="tableLabel" *ngIf="articles.length">
  <thead>
    <tr>
      <th>Header</th>
      <th>Content</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let article of articles">
      <td>{{ article.header }}</td>
      <td>{{ article.content }}</td>
    </tr>
  </tbody>
</table>
